        img = np.concatenate((np.tile(img[..., None], (1,1,3)) if img.ndim == 2 else img, 255 - 255 * mask[..., None]), axis=-1)
    with io.BytesIO() as buffer:
        imageio.imwrite(buffer, img, format)
        return buffer.getvalue()

