    url = f"{endpoint_url}/{bucket_name or ''}/{object_name or ''}"
    if query:
        url += f"?{ '&'.join(f'{k}={urllib.parse.quote(v)}' for k, v in query.items())}"
    return url
import urllib.parse

def get_target_url(endpoint_url, bucket_name=None, object_name=None, bucket_region='us-east-1', query=None):
    path = "/".join(filter(None, [endpoint_url, bucket_name, object_name]))
