from urllib.parse import urlsplit
import collections

def get_s3_endpoint(bucket_region):
    return f's3.{bucket_region}.amazonaws.com'

def is_virtual_host(endpoint_url, bucket_name):
    return 's3.amazonaws.com' in endpoint_url

def encode_object_name(object_name):
  return object_name

def queryencode(value):
  return str(value)

def get_target_url(endpoint_url, bucket_name=None, object_name=None, bucket_region='us-east-1', query=None):
    parsed_url = urlsplit(endpoint_url)
    scheme = parsed_url.scheme
    host = parsed_url.netloc
    if (scheme == 'http' and parsed_url.port == 80) or (scheme == 'https' and parsed_url.port == 443):
        host = parsed_url.hostname
    if 's3.amazonaws.com' in host:
        host = get_s3_endpoint(bucket_region)
    url = f'{scheme}://{host}'
    if bucket_name:
        is_virtual_host_style = is_virtual_host(endpoint_url, bucket_name)
        url = f'{scheme}://{bucket_name}.{host}' if is_virtual_host_style else f'{scheme}://{host}/{bucket_name}'
    url_components = [url, '/']
    if object_name:
        url_components.append(encode_object_name(object_name))
    if query:
        ordered_query = collections.OrderedDict(sorted(query.items()))
        query_components = []
        for k, v in ordered_query.items():
            if isinstance(v, list):
                for val in v:
                    query_components.append(f'{k}={queryencode(val)}')
            else:
                query_components.append(f'{k}={queryencode(v)}')
        query_string = '&'.join(query_components)
        url_components.extend(['?', query_string])
    return ''.join(url_components)

