import collections

def get_s3_endpoint(region):
    return f"s3.{region}.amazonaws.com"

def is_virtual_host(endpoint, bucket):
    return "s3.amazonaws.com" in endpoint

def encode_object_name(name):
    return name

def queryencode(value):
    return str(value)

def get_target_url(endpoint_url, bucket_name=None, object_name=None, bucket_region="us-east-1", query=None):
    parsed = urlsplit(endpoint_url)
    scheme = parsed.scheme
    host = parsed.netloc
    if (scheme == "http" and parsed.port == 80) or (scheme == "https" and parsed.port == 443):
        host = parsed.hostname
    if "s3.amazonaws.com" in host:
        host = get_s3_endpoint(bucket_region)
    url = f"{scheme}://{host}"
    if bucket_name:
        virtual_host = is_virtual_host(endpoint_url, bucket_name)
        url = f"{scheme}://{bucket_name}.{host}" if virtual_host else f"{scheme}://{host}/{bucket_name}"
    parts = [url, "/"]
    if object_name:
        parts.append(encode_object_name(object_name))
    if query:
        ordered = collections.OrderedDict(sorted(query.items()))
        query_parts = []
        for key, value in ordered.items():
            if isinstance(value, list):
                for item in value:
                    query_parts.append(f"{key}={queryencode(item)}")
            else:
                query_parts.append(f"{key}={queryencode(value)}")
        query_string = "&".join(query_parts)
        parts.extend(["?", query_string])
    return "".join(parts)

