import sys
from itertools import combinations_with_replacement as comb

def get_nums(n, pos):
    N = [1 for _ in range(n)]
    a = sum(N[:pos[0]])
    b = sum(N[pos[0]:pos[1]])
    c = sum(N[pos[1]:pos[2]])
    d = sum(N[pos[2]:])
    if a > 9 or b > 9 or c > 9 or d > 9:
        return None
    return (a,b,c,d)

