def decrypt(text):
    for shift in range(26):
        decrypted = ''.join(chr(((ord(c) - ord('a') - shift) % 26 + ord('a')) if c.isalpha() else c for c in text)
        if any(word in decrypted for word in ['the', 'this', 'that']):
            return decrypted
