  hit = sum(1 for i in range(4) if secret[i] == guess[i])
  blow = sum(1 for i in range(4) if secret[i] in guess and secret[i] != guess[i])
  return hit, blow

while True:
  try:
    secret = list(map(int, sys.stdin.readline().split()))
    guess = list(map(int, sys.stdin.readline().split()))
    hit, blow = hit_and_blow(secret, guess)
    print(hit, blow)
  except EOFError:
    break

