def get_day_of_week():
    days = ["Thursday", "Friday", "Saturday", "Sunday", "Monday", "Tuesday", "Wednesday"]
    months = [31, 29, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31]
    while True:
        try:
            m, d = map(int, input().split())
            if m == 0 and d == 0:
                break
            total_days = sum(months[:m-1]) + d
            print(days[(total_days -1) % 7])
        except ValueError:
