def hydrophobic_interactions(atom_set_a, atom_set_b):
    data = namedtuple('hydroph_interaction', 'bsatom bsatom_orig_idx ligatom ligatom_orig_idx distance restype resnr reschain restype_l resnr_l reschain_l')
    pairings = []
    for a, b in itertools.product(atom_set_a, atom_set_b):
        if a.orig_idx == b.orig_idx:
            continue
        dist = euclidean3d(a.atom.coords, b.atom.coords)
        if not config.MIN_DIST < dist < config.HYDROPH_DIST_MAX:
            continue
        restype, resnr, reschain = whichrestype(a.atom), whichresnumber(a.atom), whichchain(a.atom)
        restype_l, resnr_l, reschain_l = whichrestype(b.orig_atom), whichresnumber(b.orig_atom), whichchain(b.orig_atom)
        pairings.append(data(a.atom, a.orig_idx, b.atom, b.orig_idx, dist, restype, resnr, reschain, restype_l, resnr_l, reschain_l))
    return filter_contacts(pairings)

