def first_fit(items, capacities):
    bins = [{'capacity': c, 'items': []} for c in capacities]
    unfitted = []
    for item in items:
        placed = False
        for bin in bins:
            if item <= bin['capacity'] - sum(bin['items']):
                bin['items'].append(item)
                placed = True
                break
        if not placed:
            unfitted.append(item)
    return bins, unfitted

