def get_target_url(endpoint_url, bucket_name=None, object_name=None, bucket_region='us-east-1', query=None):
    parsed = urlsplit(endpoint_url)
    scheme = parsed.scheme
    host = parsed.netloc
    if (scheme == 'http' and parsed.port == 80) or (scheme == 'https' and parsed.port == 443):
        host = parsed.hostname
    if 's3.amazonaws.com' in host:
        host = get_s3_endpoint(bucket_region)
    url = f'{scheme}://{host}'
    if bucket_name:
        is_virtual = is_virtual_host(endpoint_url, bucket_name)
        url = f'{scheme}://{bucket_name}.{host}' if is_virtual else f'{scheme}://{host}/{bucket_name}'
    path = '/' + encode_object_name(object_name) if object_name else '/'
    query_str = ''
    if query:
        query_items = sorted(query.items())
        query_params = []
        for k, v in query_items:
            if isinstance(v, list):
                for item in v:
                    query_params.append(f'{k}={queryencode(item)}')
            else:
                query_params.append(f'{k}={queryencode(v)}')
        query_str = '?' + '&'.join(query_params) if query_params else ''

    return url + path + query_str
