def save(self, filething=None, padding=None):
    if filething is None:
        filething = self._filename
    if filething is None:
        raise MutagenError("No file to save to")

    try:
        with open(filething, "rb+") as file:
            data = self._render()
            if padding:
                data = padding(data)
            file.seek(0)
            file.write(data)
            file.truncate()
    except IOError as e:
        raise MutagenError(f"Error saving file: {e}")

