def clean_series_name(seriesname):
    cleaned = seriesname.replace('_', ' ').replace('.', ' ')
    parts = cleaned.split()
    result = []
    for i, part in enumerate(parts):
        if i > 0 and part.replace('.', '').isdigit() and parts[i-1].replace('.', '').isdigit():
            result.append('.' + part)
        else:
            result.append(part)
    return ' '.join(result).strip('-')

