def list_directory_contents(path, include_hidden=False):
    """Retrieves the contents of a specified directory.

    :param path: The directory to list files and folders from.
    :param include_hidden: If True, hidden files will be included in the list.
    :return: A list of file and folder paths in the specified directory.
    """
    contents = []
    for entry in os.listdir(path):
        if (entry.startswith('.') and include_hidden) or (not entry.startswith('.')):
            contents.append(os.path.join(path, entry))
    return list(set(contents))

import os

def ls_dir(path, include_hidden=False):
    """Finds content of folder

    :param path: directory to get list of files and folders
    :param include_hidden: True iff include hidden files in list
    :return: List of paths in given directory
    """
    return [
        os.path.join(path, file)
        for file in os.listdir(path)
        if (FileSystem(file).is_hidden() and include_hidden) or not FileSystem(file).is_hidden()
    ]

