    """
    if configuration is None:
        configuration = self.default_configuration

    if tpl is None:
        tpl = self.default_template

    if keys is None:
        keys = {}

    if obj_type is None:
        obj_type = self.determine_obj_type(keys, tpl)

    user_object = UserObject(configuration, obj_type)
    user_object.apply_template(tpl)
    user_object.set

