def flatten(obj, isinstance=isinstance, StringTypes=(str, bytes), SequenceTypes=(list, tuple)):
    def do_flatten(sequence):
        for item in sequence:
            if isinstance(item, StringTypes):
                yield item
            elif isinstance(item, SequenceTypes):
                yield from do_flatten(item)
            else:
                yield item

    if isinstance(obj, StringTypes):
        return [obj]
    elif isinstance(obj, SequenceTypes):
        return list(do_flatten(obj))
    else:
        return [obj]

