def on_binlog(event, stream):
    event_dict = event.__dict__
    table_name = event.table
    schema_name = event.schema

    if isinstance(event, WriteRowsEvent):
        for row in event.rows:
            stream.send(f"INSERT:{schema_name}.{table_name}", row)
    elif isinstance(event, UpdateRowsEvent):
        for row in event.rows:
            stream.send(f"UPDATE:{schema_name}.{table_name}", row)
    elif isinstance(event, DeleteRowsEvent):
        for row in event.rows:
            stream.send(f"DELETE:{schema_name}.{table_name}", row)

    stream.send(f"SCHEMA:{schema_name}", event_dict)
    stream.send(f"TABLE:{schema_name}.{table_name}", event_dict)
    stream.send("BINLOG", event_dict)

