def _validate_condition_keys(self, field, value, error):
    """
    Validates that all of the keys in one of the sets of keys are defined
    as keys of ``value``.
    """
    if not isinstance(value, dict):
        error(field, "Must be a dictionary")
    else:
        valid_keys = set(self.condition_keys)
        provided_keys = set(value.keys())
        if not valid_keys.issuperset(provided_keys):
            invalid_keys = provided_keys - valid_keys
            error(field, f"Invalid keys: {', '.join(invalid_keys)}")

