def L1(layer="input", constant=0, batch=None):
    """L1 norm of layer. Generally used as penalty."""
    import tensorflow as tf

    if batch is None:
        batch = tf.shape(layer)[0]

    l1_norm = tf.reduce_sum(tf.abs(layer), axis=list(range(1, len(layer.shape))))
    l1_norm = tf.reshape(l1_norm, [batch, -1])

    return constant * tf.reduce_sum(l1_norm)

