import math

HYDROPH_DIST_MAX = 5.0  # Maximum distance for hydrophobic interaction

def distance(atom1, atom2):
    return math.sqrt((atom1['x'] - atom2['x'])**2 + (atom1['y'] - atom2['y'])**2 + (atom1['z'] - atom2['z'])**2)

def is_hydrophobic(atom):
    return atom['element'] == 'C'

def hydrophobic_interactions(atom_set_a, atom_set_b):
    """Detection of hydrophobic interactions between atom_set_a (binding site) and atom_set_b (ligand).
    Definition: All pairs of qualified carbon atoms within a distance of HYDROPH_DIST_MAX
    """
    interactions = []
    for atom_a in atom_set_a:
        if is_hydrophobic(atom_a):
            for atom_b in atom_set_b:
                if is_hydrophobic(atom_b):
                    if distance(atom_a, atom_b) <= HYDROPH_DIST_MAX:
                        interactions.append((atom_a, atom_b))
    return interactions

