def ff(items, targets):
    """First-Fit

    This is perhaps the simplest packing heuristic;
    it simply packs items in the next available bin.

    Complexity O(n^2)
    """
    bins = []
    for item in items:
        placed = False
        for bin in bins:
            if sum(bin) + item <= targets:
                bin.append(item)
                placed = True
                break
        if not placed:
            bins.append([item])
    return bins

