def _count_extra_actions(self, game_image):
    """Count the number of extra actions for player in this turn."""
    extra_actions = 0
    for event in game_image['events']:
        if event['type'] == 'extra_action' and event['player_id'] == self.player_id:
            extra_actions += 1
    return extra_actions

