class KMeansPredictor:
    def __init__(self, cluster_centers):
        self.cluster_centers = np.array(cluster_centers)

    def predict(self, X):
        X = np.array(X)
        distances = np.linalg.norm(X[:, np.newaxis] - self.cluster_centers, axis=2)
        labels = np.argmin(distances, axis=1)
        return labels

