import re

class Router:
    def __init__(self):
        self.routes = []

    def add_router(self, path, router):
        """
        Add a (regex, router) pair to this router. Any req.path that
        matches the regex will pass the request/response objects to
        that router.
        """
        self.routes.append((re.compile(path), router))

    def match(self, req_path):
        for pattern, router in self.routes:
            if pattern.match(req_path):
                return router
        return None

