class CommandIterator:
    def __init__(self, command_output):
        self.command_output = command_output.splitlines()
        self.index = 0

    def __iter__(self):
        return self

    def next(self):
        """ allow us to iterate over the output of our command """
        if self.index < len(self.command_output):
            result = self.command_output[self.index]
            self.index += 1
            return result
        else:
            raise StopIteration

    # For compatibility with Python 3
    def __next__(self):
        return self.next()

