    if (scheme == 'http' and parsed_url.port == 80) or (scheme == 'https' and parsed_url.port == 443):
        host = parsed_url.hostname

    if 's3.amazonaws.com' in host:
        host = get_s3_endpoint(bucket_region)

    url = f"{scheme}://{bucket_name + '.' + host if bucket_name and is_virtual_host(endpoint_url, bucket_name) else host}/{bucket_name}" if bucket_name else f"{scheme}://{host}"

    url_components = [url, '/', encode_object_name(object_name) if object_name else '']

