def count_hits_blows(secret, guess):
    correct, misplaced = 0, 0
    for s, g in zip(secret, guess):
        if s == g:
            correct += 1
        elif g in secret:
            misplaced += 1
    return (correct, misplaced)

while True:
    try:
        secret_sequence = input().split()
        guess_sequence = input().split()
    except:
        break
    hits, blows = count_hits_blows(secret_sequence, guess_sequence)
    print(hits, blows)

