    x, y = 0, 0
    angle = 90
    while True:
        r, d = map(int, input().split(','))
        if r == 0 and d == 0:
            break
        dx, dy = move(angle)
        angle += -d
        x += r * dx
        y += r * dy
    print(int(round(x)))
    print(int(round(y)))

