def solve_equations():
    for line in sys.stdin:
        a, b, c, d, e, f = map(float, line.split())
        try:
            x = (c * e - b * f) / (a * e - b * d)
            y = (a * f - c * d) / (a * e - b * d)
            print(f"{x:.3f} {y:.3f}")
        except ZeroDivisionError:
