import math
def saidai(x, y):
    x, y = math.fabs(x), math.fabs(y)
    y, x = max(x, y), min(x, y)
    if x == 0:
        return y

    return saidai(x, y % x)

def saisyo(x, y):
    spam = saidai(x, y)
    return spam * (x / spam) * (y / spam)

