def sum_of_4_integers(datasets):
    from itertools import combinations_with_replacement
    results = []
    for n in datasets:
        count = 0
        for combo in combinations_with_replacement(range(10), 4):
            if sum(combo) == n:
                count += len(set(list(permutations(combo))))
        results.append(count)
