class Stack:
    def __init__(self, maxsize=100):
        self.maxsize = maxsize
        self.array = [0] * maxsize
        self.index = 0

    def push(self, data):
        if self.index < self.maxsize:
            self.array[self.index] = data
            self.index += 1
