    data = sys.stdin.read().split()
    n = int(data[0])
    results = []
    for i in range(1, len(data), 6):
        x1, y1, r1, x2, y2, r2 = map(float, data[i:i+6])
        d2 = (x1 - x2)**2 + (y1 - y2)**2
        if d2 < (r1 - r2)**2:
            results.append(2 if r1 > r2 else -2)
        elif d2 > (r1 + r2)**2:
            results.append(0)
        else:
            results.append(1)
    print('\n'.join(map(str, results)))

