import datetime

def get_day(month, day):
    try:
        date = datetime.date(2000, month, day)  # Year doesn't matter for day of week
        return date.strftime("%A")
    except ValueError:
        return None

def main():
    while True:
        try:
            month, day = map(int, input().split())
            if month == 0:
                break
            day_name = get_day(month, day)
            if day_name:
                print(day_name)

        except ValueError:
            break

if __name__ == "__main__":
    main()

