def get_day_of_week(month, day):
    try:
        date = datetime.date(2024, month, day)  # Use a fixed year (leap year)
        return date.strftime("%A")
    except ValueError:
        return None

def main():
    while True:
        try:
            month, day = map(int, input().split())
            if month == 0:
                break
            result = get_day_of_week(month, day)
            if result:
                print(result)
        except ValueError:
            break

