import sys

def solve_linear_equations(a, b, c, d, e, f):
    det = a * e - b * d
    if det == 0:
        return None  # Handle the case for no solution
    x = (e * c - b * f) / det
    y = (-d * c + a * f) / det
    return (x, y)

