def count_combinations(n):
    count = 0
    for a in range(10):
        for b in range(10):
            for c in range(10):
                d = n - a - b - c
                if 0 <= d <= 9:
                    count += 1
    return count

def main():
    while True:
        try:
            n = int(input())
            if 1 <= n <= 50:
                print(count_combinations(n))
        except EOFError:
            break

if __name__ == "__main__":
    main()

