def count_primes(n):
    if n < 2:
        return 0
    sieve = [True] * (n + 1)
    sieve[0] = sieve[1] = False
    for i in range(2, int(n**0.5) + 1):
        if sieve[i]:
            for j in range(i*i, n + 1, i):
                sieve[j] = False
    return sum(sieve)

def main():
    while True:
        try:
            n = int(input())
            print(count_primes(n))
        except EOFError:
            break

if __name__ == "__main__":
