def caesar_cipher_decoder(encrypted_text):
    for shift in range(26):
        decrypted = ''
        for char in encrypted_text:
            if char.isalpha():
                ascii_offset = ord('a')
                decrypted += chr((ord(char) - ascii_offset - shift) % 26 + ascii_offset)
            else:
                decrypted += char
        if any(word in decrypted for word in ['the', 'this', 'that']):
            return decrypted
    return "No valid decryption found"

