    months = [31, 29, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31]

    while True:
        input_data = input().split()
        if input_data == ['0', '0']:
            break

        m, d = map(int, input_data)
        total_days = sum(months[:m - 1]) + d - 1
        print(days[total_days % 7])

