def check_parallelism():
    n = int(input())
    for _ in range(n):
        x1, y1, x2, y2, x3, y3, x4, y4 = map(float, input().split())
        if (x2 - x1) == 0 and (x4 - x3) == 0:
            print("YES" if (x1 == x3) else "NO")
        elif (x2 - x1) == 0 or (x4 - x3) == 0:
            print("NO")
        else:
            slope_ab = (y2 - y1) / (x2 - x1)
            slope_cd = (y4 - y3) / (x4 - x3)
            print("YES" if abs(slope_ab - slope_cd) < 1e-6 else "NO")

